    private void BotonSumaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BotonSumaMouseClicked
        if (Numero1 == 0){
            Numero1 = Double.parseDouble(Pantalla.getText());
            Pantalla.setText("");
        }
        else
        {
            Numero2 = Double.parseDouble(Pantalla.getText());
            Pantalla.setText(String.valueOf(fun.Suma(Numero1, Numero2))); //ejecutar metodo suma
            Numero1 = Numero2;
            Numero2 = 0;
        }        
        op = '+';
    }//GEN-LAST:event_BotonSumaMouseClicked

